mapper.update:
UPDATE Board
SET viewCnt = viewCnt+1
WHERE boardId = #{boardId}

UPDATE Board
SET title = #{title}, content = #{content}, regDate = now()
WHERE boardId = #{boardId}

UPDATE Board
SET viewCnt = viewCnt+1
WHERE boardId = #{boardId}

UPDATE Board
SET viewCnt = viewCnt-1
WHERE boardId = #{boardId}
