mapper.update:
UPDATE User
SET userId = #{userId}, password = #{password}, nickname = #{nickname}, birth = #{birth}, email = #{email}, phonenum = #{phonenum}, address = #{address}
WHERE userId = #{userId}

UPDATE User
SET cash = money + #{cash}, totalcash = money + #{totalcash}
WHERE userId = #{userId}

UPDATE User
SET cash = #{cash} - money
WHERE userId = #{userId}
